"use strict";(self.webpackChunkeleven_plus_core_concepts=self.webpackChunkeleven_plus_core_concepts||[]).push([[3094],{3094:e=>{e.exports=JSON.parse('{"concept":"Profit and Loss","subject":"mathematics","category":"Time and Money","explanation":"Profit is when you sell something for more than you paid for it. Loss is when you sell for less. Cost Price (CP) is what you pay, Selling Price (SP) is what you sell for. Profit = SP - CP. Loss = CP - SP. Percentage profit = (Profit \xf7 CP) \xd7 100. This is useful for understanding business and money in real life!","examples":[{"question":"Tom buys a football for \xa320 and sells it for \xa328. What is his profit?","solution":"\xa38 profit","explanation":"Profit = Selling Price - Cost Price = \xa328 - \xa320 = \xa38. Tom made \xa38 profit."},{"question":"Sarah bought a book for \xa315 and had to sell it for \xa312. What is her loss percentage?","solution":"20% loss","explanation":"Loss = \xa315 - \xa312 = \xa33. Loss percentage = (\xa33 \xf7 \xa315) \xd7 100 = 20%"}],"exercises":[{"type":"multiple-choice","question":"Alex buys a video game for \xa330 and sells it for \xa336. What is his profit percentage?","options":["20%","15%","25%","10%"],"correct":[0],"feedback":["Correct! Profit = \xa36, so (\xa36 \xf7 \xa330) \xd7 100 = 20%","Close! Check your calculation: \xa36 profit on \xa330 cost","Too high - the profit is \xa36, not \xa37.50","Too low - the profit percentage is higher than 10%"]},{"type":"multiple-choice","question":"A trader bought 10 apples for \xa35 and sold them for 60p each. What is his total profit?","options":["\xa31","\xa36","\xa311","50p"],"correct":[0],"feedback":["Correct! Sold for 10 \xd7 60p = \xa36. Profit = \xa36 - \xa35 = \xa31","This is the selling price, not the profit","This is the selling price plus cost price, not profit","Check your calculation - profit is higher than this"]}]}')}}]);