"use strict";(self.webpackChunkeleven_plus_core_concepts=self.webpackChunkeleven_plus_core_concepts||[]).push([[559],{559:e=>{e.exports=JSON.parse('{"concept":"Inequalities","subject":"mathematics","category":"Algebra","explanation":"Inequalities compare values using >, <, \u2265, \u2264 symbols. > means \'greater than\', < means \'less than\', \u2265 means \'greater than or equal to\', \u2264 means \'less than or equal to\'. Solve like equations, but if you multiply or divide by a negative number, flip the inequality sign!","examples":[{"question":"Solve: x + 5 > 12","solution":"x > 7","explanation":"Subtract 5 from both sides: x + 5 - 5 > 12 - 5, so x > 7. This means x can be any number greater than 7."},{"question":"If 3x \u2264 15, what values can x take?","solution":"x \u2264 5","explanation":"Divide both sides by 3: 3x \xf7 3 \u2264 15 \xf7 3, so x \u2264 5. This means x can be 5 or any number less than 5."}],"exercises":[{"type":"multiple-choice","question":"Solve: 2x + 3 < 11","options":["x < 4","x < 7","x > 4","x < 14"],"correct":[0],"feedback":["Correct! 2x + 3 < 11 \u2192 2x < 8 \u2192 x < 4","You forgot to divide by 2 after subtracting 3","The inequality sign should be <, not >","This would be 2x < 11, but you need to subtract 3 first"]},{"type":"multiple-choice","question":"Which value of x satisfies: x - 2 \u2265 5?","options":["x = 6","x = 7","x = 8","All of the above"],"correct":[3],"feedback":["x = 6 works, but so do larger values","x = 7 works, but it\'s not the only answer","x = 8 works, but other values do too","Correct! x \u2265 7, so 6, 7, 8 and any number \u2265 7 all work"]}]}')}}]);