"use strict";(self.webpackChunkeleven_plus_core_concepts=self.webpackChunkeleven_plus_core_concepts||[]).push([[3638],{3638:e=>{e.exports=JSON.parse('{"concept":"Percentage Increase and Decrease","subject":"mathematics","category":"Percentages and Fractions","explanation":"To calculate percentage increase: New value - Original value, then divide by original value and multiply by 100. For percentage decrease: Original value - New value, then divide by original value and multiply by 100. To find the new value after a percentage change: multiply the original by (100 \xb1 percentage)/100.","examples":[{"question":"A price increases from \xa380 to \xa396. What is the percentage increase?","solution":"20%","explanation":"Increase = \xa396 - \xa380 = \xa316. Percentage increase = (\xa316 \xf7 \xa380) \xd7 100 = 20%"},{"question":"A population decreases from 500 to 400. What is the percentage decrease?","solution":"20%","explanation":"Decrease = 500 - 400 = 100. Percentage decrease = (100 \xf7 500) \xd7 100 = 20%"}],"exercises":[{"type":"multiple-choice","question":"A shirt costs \xa340. After a 15% increase, what is the new price?","options":["\xa346","\xa334","\xa355","\xa345"],"correct":[0],"feedback":["Correct! New price = \xa340 \xd7 1.15 = \xa346","This would be a decrease, not an increase.","This is too high - you may have added 15 instead of 15%.","Close, but check your calculation: \xa340 \xd7 1.15 = \xa346"]},{"type":"multiple-choice","question":"A value decreases from 200 to 160. What is the percentage decrease?","options":["25%","20%","40%","15%"],"correct":[1],"feedback":["Check your calculation: (40 \xf7 200) \xd7 100 = 20%","Correct! Decrease = 40, so (40 \xf7 200) \xd7 100 = 20%","This would be if 160 decreased to 200, not the other way around.","This is too small for a decrease from 200 to 160."]}]}')}}]);