"use strict";(self.webpackChunkeleven_plus_core_concepts=self.webpackChunkeleven_plus_core_concepts||[]).push([[4295],{4295:e=>{e.exports=JSON.parse('{"concept":"Simple Interest","subject":"mathematics","category":"Percentages and Fractions","explanation":"Simple interest is calculated using the formula: Interest = Principal \xd7 Rate \xd7 Time. Principal is the original amount, Rate is the percentage per year (as a decimal), and Time is in years. Simple interest doesn\'t compound - it\'s calculated only on the original amount. Total amount = Principal + Interest.","examples":[{"question":"Calculate the simple interest on \xa3500 at 4% per year for 3 years.","solution":"\xa360","explanation":"Interest = \xa3500 \xd7 0.04 \xd7 3 = \xa360. Total amount = \xa3500 + \xa360 = \xa3560."},{"question":"How much interest is earned on \xa3200 at 5% per year for 2 years?","solution":"\xa320","explanation":"Interest = \xa3200 \xd7 0.05 \xd7 2 = \xa320. Total amount = \xa3220."}],"exercises":[{"type":"multiple-choice","question":"What is the simple interest on \xa3300 at 6% per year for 4 years?","options":["\xa360","\xa372","\xa318","\xa324"],"correct":[1],"feedback":["This would be 6% for only 2 years: \xa3300 \xd7 0.06 \xd7 2 = \xa336.","Correct! Interest = \xa3300 \xd7 0.06 \xd7 4 = \xa372.","This is 6% for only 1 year: \xa3300 \xd7 0.06 \xd7 1 = \xa318.","This is too low - check your calculation."]},{"type":"multiple-choice","question":"If \xa3400 earns \xa348 simple interest in 3 years, what is the annual rate?","options":["3%","4%","5%","6%"],"correct":[1],"feedback":["This is too low. Try: Interest \xf7 (Principal \xd7 Time) = Rate.","Correct! \xa348 \xf7 (\xa3400 \xd7 3) = 0.04 = 4%.","This is too high for the given interest amount.","This rate would give \xa372 interest, not \xa348."]}]}')}}]);